import re
import json 
def extract_href(input_text):
    """
    Extracts all href values from the given text.

    Parameters:
        input_text (str): The text to process.

    Returns:
        list: A list of href values found in the text.
    """
    href_pattern = r'href="([^"]+)"'
    return re.findall(href_pattern, input_text)

